import React, { useState } from "react";
import axios from "axios";
import {
  Container,
  Typography,
  Button,
  Box,
  CircularProgress,
  Card,
  CardContent,
  Divider,
} from "@mui/material";
import { Document, Page, pdfjs } from "react-pdf";


function App() {
  const [file, setFile] = useState(null);
  const [downloadURL, setDownloadURL] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setDownloadURL(null);
    setPreviewBlob(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      alert("Please select a PDF file first.");
      return;
    }

    setLoading(true);
    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await axios.post("http://localhost:8000/annotate/", formData, {
        responseType: "blob",
      });

      const blob = new Blob([res.data], { type: "application/pdf" });
      const url = window.URL.createObjectURL(blob);
      setDownloadURL(url);
      setPreviewBlob(blob);
    } catch (err) {
      alert("Error processing file.");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="md" sx={{ mt: 8 }}>
      <Card elevation={3}>
        <CardContent>
          <Typography variant="h4" align="center" gutterBottom>
            Mega Meta Research Paper Annotator
          </Typography>
          <Divider sx={{ my: 2 }} />

          <Box component="form" onSubmit={handleSubmit} textAlign="center">
            <Button variant="outlined" component="label" sx={{ mb: 2 }}>
              {file ? file.name : "Choose PDF File"}
              <input
                type="file"
                accept="application/pdf"
                hidden
                onChange={handleFileChange}
              />
            </Button>

            <Button
              variant="contained"
              type="submit"
              sx={{ ml: 2 }}
              disabled={loading}
            >
              {loading ? <CircularProgress size={24} color="inherit" /> : "Submit"}
            </Button>
          </Box>

          {downloadURL && (
            <Box textAlign="center" mt={3}>
              <Button
                variant="outlined"
                color="success"
                href={downloadURL}
                download="annotated.pdf"
              >
                Download Annotated PDF
              </Button>
            </Box>
          )}
        </CardContent>
      </Card>

      {/* PDF Preview */}
      {previewBlob && (
        <Box mt={4} textAlign="center">
            <Typography variant="h6" gutterBottom>
                Annotated PDF Preview
            </Typography>

            {/* ðŸ”¹ Full Screen Toggle Button */}
            <Button
                variant="outlined"
                sx={{ mb: 2 }}
                onClick={toggleFullScreen}
            >
                {isFullScreen ? "Exit Full Screen" : "Enter Full Screen"}
            </Button>

            {/* ðŸ”¹ PDF Viewer Container */}
            <Box
                ref={previewRef}  // ðŸ”¹ Attach the fullscreen ref here
                sx={{
                    border: "1px solid #ccc",
                    borderRadius: 2,
                    height: "500px",
                    overflowY: "scroll",
                    padding: 2,
                    backgroundColor: "#f9f9f9",
                }}
            >
                <Document
                    file={previewBlob}
                    onLoadSuccess={({ numPages }) => setNumPages(numPages)}
                >
                    {Array.from(new Array(numPages), (el, index) => (
                    <   Page key={`page_${index + 1}`} pageNumber={index + 1} />
                    ))}
                </Document>
            </Box>
        </Box>
)}
    </Container>
  );
}

export default App;
